% %% %% %% %% %% %% Read the mesh  %fileID = fopen('./Projet/cube_01.mesh','r');fileID2 = fopen('./Projet/cube_01_2D.mesh','w');% read empty lineline = fgets(fileID);fprintf(fileID2, 'MeshVersionFormatted 2\n');line = fgets(fileID);fprintf(fileID2, 'Dimension\n');line = fgets(fileID);fprintf(fileID2, '2\n');line = fgets(fileID);fprintf(fileID2, 'Vertices\n');% read nvline = fgets(fileID);nv = sscanf(line, '%i');fprintf(fileID2, '%i\n', nv);% get nodesfor i = 1:nv    line = fgets(fileID);    tmp = sscanf(line, '%f %f', 2);    fprintf(fileID2, '%f %f %i\n', tmp(1), tmp(2), 1);end% read empty lineline = fgets(fileID);fprintf(fileID2, 'Edges\n');% read nbline = fgets(fileID);nb = sscanf(line, '%i');fprintf(fileID2, '%i\n', nb);% get boundariesfor i = 1:nb    line = fgets(fileID);    tmp = sscanf(line, '%i %i %i', 3);    fprintf(fileID2, '%i %i %i\n', tmp(1), tmp(2), tmp(3));end% read number of trianglesline = fgets(fileID);line = fgets(fileID);ne = sscanf(line, '%i');fprintf(fileID2, 'Triangles\n');fprintf(fileID2, '%i\n', ne);% read trianglesfor i = 1:ne   line = fgets(fileID);   Tri = sscanf(line, '%i %i %i %i', 4);   fprintf(fileID2, '%i %i %i %i\n', Tri);endfprintf(fileID2, 'End');fclose(fileID);fclose(fileID2);